mapper.select:
select count(*) 
from tuna_notify 
where NOTIFYTYPE = #{notifyType} 
and donotifyMemberCode= #{doNotifyMemberCode}

select count(*) 
from tuna_notify 
where NOTIFYTYPE = #{messageCode}

select notifycode, notifytype, notifytime, donotifymembercode, notifymembercode
from tuna_notify
where notifytarget = #{target}
